INSERT INTO `block_content` VALUES (32,32,'basic','c8e14978-d9d3-41fc-81de-c2757eebe954','en');
INSERT INTO `block_content__body` VALUES ('basic',0,32,32,'en',0,'<p>ICRP’s aim is to promote international cancer research collaboration and avoid duplication. We collaborate with many other organizations internationally to improve outcomes for cancer patients. Use the links below to find out more.</p>\r\n\r\n<h5>Cancer prevention and cancer control:</h5>\r\n\r\n<ul>\r\n	<li><a href=\"https://www.uicc.org/who-we-are/about-us\" target=\"_blank\">Union for International Cancer Control (UICC)</a></li>\r\n	<li><a href=\"https://www.iarc.fr/\" target=\"_blank\">International Agency for Research on Cancer (IARC)</a></li>\r\n</ul>\r\n\r\n<h5>Cancer Screening</h5>\r\n\r\n<ul>\r\n	<li><a href=\"https://healthcaredelivery.cancer.gov/icsn/\" target=\"_blank\">International Cancer Screening Network (ICSN)</a></li>\r\n</ul>\r\n\r\n<h5>Cancer Trials</h5>\r\n\r\n<ul>\r\n<li><a href=\"http://www.irci.info/\" target=\"_blank\">International Rare Cancers Clinical Trials Initiative</a></li>\r\n</ul>\r\n','','full_html');
