--
-- Update FAQ Node (entity_id 167)
--

UPDATE `node__body`
    SET body_value = '<p>We estimate that our database covers around 60-70% of world cancer research funding. We welcome applications for <a href=\"/become-a-partner\">membership</a> from cancer research funders worldwide to enhance our coverage.</p>\r\n\r\n<p>Data on the ICRP website is updated regularly, according to our partners’ funding and data collection schedules. Data for the most recent calendar years may be incomplete. For details on recent uploads visit our <a href=\"/partners\">ICRP Partners and Funding Organizations</a> page</p>\r\n\r\n<p>&nbsp;</p>\r\n'
    WHERE entity_id = 167;

UPDATE `node_revision__body`
    SET body_value = '<p>We estimate that our database covers around 60-70% of world cancer research funding. We welcome applications for <a href=\"/become-a-partner\">membership</a> from cancer research funders worldwide to enhance our coverage.</p>\r\n\r\n<p>Data on the ICRP website is updated regularly, according to our partners’ funding and data collection schedules. Data for the most recent calendar years may be incomplete. For details on recent uploads visit our <a href=\"/partners\">ICRP Partners and Funding Organizations</a> page</p>\r\n\r\n<p>&nbsp;</p>\r\n'
